using System;
using System.Diagnostics;
using System.IO;
using System.Text;
using System.Threading;
using CSharpHelpers.DotNetZipPackage;

namespace CSharpHelpers
{
    class Program
    {
        static void Main(string[] args)
        {
            ZipPackageTest();
        }

        private static void ZipPackageTest()
        {
            var packageSaveLocation = string.Format("{0}\\{1}", Environment.GetFolderPath(Environment.SpecialFolder.MyDocuments), "package.zip");

            var googleLogoData = "";
            var htmlFileData = "<html><body><a href=\"http://www.google.com\"><img src=\"google-logo-370x229.jpg\"></a></body></html>";

            var googleLogoStream = new MemoryStream(Convert.FromBase64String(googleLogoData));
            var htmlFileStream = new MemoryStream(Encoding.UTF8.GetBytes(htmlFileData));

            using (var package = new ZipPackage())
            {
                package.Add(ZipPackageItemType.HTMLFile, "index.htm", htmlFileStream);
                package.Add(ZipPackageItemType.Graphic, "google-logo-370x229.jpg", googleLogoStream);

                foreach (var item in package.GetItemNames())
                    Console.WriteLine("Added item: " + item);

                using (var file = new FileStream(packageSaveLocation, FileMode.Create))
                    package.ToStream().CopyTo(file);

                Console.WriteLine("Package saved to: " + packageSaveLocation);
            }

            Console.WriteLine("Reading package from " + packageSaveLocation);
            using (var package = new ZipPackage(new MemoryStream(File.ReadAllBytes(packageSaveLocation))))
            {
                foreach (var item in package.GetItemNames())
                    Console.WriteLine("Retrieved item: " + item);
            }
        }

